	uint16 aux_adc_vmid_rev7_core0[] = {0x8e, 0x96, 0x96, 0x96};
	uint16 aux_adc_vmid_rev7_core1[] = {0x8f, 0x9f, 0x9f, 0x96};
	uint16 aux_adc_vmid_rev4[] = {0xa2, 0xb4, 0xb4, 0x89};
