void
wlc_phy_txpwrctrl_pwr_setup_rev19_nphy(phy_info_t *pi, uint8 avg_en, uint8 analog_lpf_bw,
int16 a1[], int16 b0[], int16 b1[])
{

	uint8 enable = avg_en;
	uint16 est_pwr_tbl_len = 64;
	uint16 est_pwr_tbl_start_ind = 0;
	uint16 ctr;
	int16 idx2tbl, tssi, pwr_est;
	uint16 kk;

#ifdef TWO_PWR_RANGE
	int16 a1lo[2] = {0, 0};
	int16 b0lo[2] = {0, 0};
	int16 b1lo[2] = {0, 0};

	wlc_phy_get_paparams_lo_nphy(pi, a1lo, b0lo, b1lo);
#endif

	wlc_phy_rev3_setup_rfiqcal_mux(pi, 1, 0, 0);
	/* 4324 TBD : What is the LPF BW setting?
	 */
	phy_reg_mod(pi, NPHY_REV19_TxPwrCtrlAvgrNnum,
		NPHY_REV19_TxPwrCtrlAvgrNnum_tssiavgrEn_MASK,
		enable << NPHY_REV19_TxPwrCtrlAvgrNnum_tssiavgrEn_SHIFT);
	phy_reg_mod(pi, NPHY_REV19_TxPwrCtrlAvgrNnum,
		NPHY_REV19_TxPwrCtrlAvgrNnum_Ntssi_intg_log2_MASK,
		0x6 << NPHY_REV19_TxPwrCtrlAvgrNnum_Ntssi_intg_log2_SHIFT);
	phy_reg_mod(pi, NPHY_REV19_TxPwrCtrlAvgrNnum,
		NPHY_REV19_TxPwrCtrlAvgrNnum_Ntssi_avgr_delay_MASK,
		0xa0 << NPHY_REV19_TxPwrCtrlAvgrNnum_Ntssi_avgr_delay_SHIFT);

	if (NREV_IS(pi->pubpi.phy_rev, LCNXN_BASEREV + 4)) {
		phy_reg_mod(pi, NPHY_REV19_TxPwrCtrlNum_Vbat,
			NPHY_REV19_TxPwrCtrlNum_Vbat_Nvbat_intg_log2_MASK,
			6 << NPHY_REV19_TxPwrCtrlNum_Vbat_Nvbat_intg_log2_SHIFT);
		phy_reg_mod(pi, NPHY_REV19_TxPwrCtrlNum_Vbat,
			NPHY_REV19_TxPwrCtrlNum_Vbat_Nvbat_delay_MASK,
			10 << NPHY_REV19_TxPwrCtrlNum_Vbat_Nvbat_delay_SHIFT);
		phy_reg_mod(pi, NPHY_REV19_TxPwrCtrlNum_temp,
			NPHY_REV19_TxPwrCtrlNum_temp_Ntemp_intg_log2_MASK,
			6 << NPHY_REV19_TxPwrCtrlNum_temp_Ntemp_intg_log2_SHIFT);
		phy_reg_mod(pi, NPHY_REV19_TxPwrCtrlNum_temp,
			NPHY_REV19_TxPwrCtrlNum_temp_Ntemp_delay_MASK,
			10 << NPHY_REV19_TxPwrCtrlNum_temp_Ntemp_delay_SHIFT);
	}

	phy_reg_mod(pi, NPHY_TxPwrCtrlNewFeatureEnables,
		NPHY_TxPwrCtrlNewFeatureEnables_bbShftEn_MASK,
		1 << NPHY_TxPwrCtrlNewFeatureEnables_bbShftEn_SHIFT);

	phy_reg_mod(pi, NPHY_REV19_bbShiftCtrl,
		NPHY_REV19_bbShiftCtrl_bbshift_mode_core0_MASK,
		1 << NPHY_REV19_bbShiftCtrl_bbshift_mode_core0_SHIFT);
	phy_reg_mod(pi, NPHY_REV19_bbShiftCtrl,
		NPHY_REV19_bbShiftCtrl_bbshift_mode_core1_MASK,
		1 << NPHY_REV19_bbShiftCtrl_bbshift_mode_core1_SHIFT);

	phy_reg_mod(pi, NPHY_TxPwrCtrlDamping,
		NPHY_TxPwrCtrlDamping_DeltaPwrDamp_MASK,
		16 << NPHY_TxPwrCtrlDamping_DeltaPwrDamp_SHIFT);
	phy_reg_mod(pi, NPHY_TxPwrCtrlDamping,
		NPHY_TxPwrCtrlDamping_DeltaPwrLimit_MASK,
		8 << NPHY_TxPwrCtrlDamping_DeltaPwrLimit_SHIFT);
	phy_reg_mod(pi, NPHY_TxPwrCtrlNnum,
		NPHY_TxPwrCtrlNnum_Npt_intg_log2_MASK,
		3 << NPHY_TxPwrCtrlNnum_Npt_intg_log2_SHIFT);

	if (NREV_IS(pi->pubpi.phy_rev, LCNXN_BASEREV + 4)) {
		if (CHSPEC_IS2G(pi->radio_chanspec)) {
			phy_reg_mod(pi, NPHY_TxPwrCtrlcckPwrOffset,
			NPHY_TxPwrCtrlcckPwrOffset_cckPwrOffsetEn_MASK,
			0x1 << NPHY_TxPwrCtrlcckPwrOffset_cckPwrOffsetEn_SHIFT);
			phy_reg_mod(pi, NPHY_TxPwrCtrlcckPwrOffset,
			NPHY_TxPwrCtrlcckPwrOffset_cckPwrOffset_MASK,
			0x5 << NPHY_TxPwrCtrlcckPwrOffset_cckPwrOffset_SHIFT);  /* 0.625dB */
			phy_reg_mod(pi, NPHY_TxPwrCtrlcckPwrOffset,
			NPHY_TxPwrCtrlcckPwrOffset_bw40PwrOffsetEn_MASK,
			0x0 << NPHY_TxPwrCtrlcckPwrOffset_bw40PwrOffsetEn_SHIFT);
		} else {
			/* 40MHz offset 0.5dB */
			phy_reg_mod(pi, NPHY_TxPwrCtrlNewFeatureEnables,
			NPHY_TxPwrCtrlNewFeatureEnables_bw40PwrOffset_MASK,
			4 << NPHY_TxPwrCtrlNewFeatureEnables_bw40PwrOffset_SHIFT);
			phy_reg_mod(pi, NPHY_TxPwrCtrlcckPwrOffset,
			NPHY_TxPwrCtrlcckPwrOffset_bw40PwrOffsetEn_MASK,
			0x1 << NPHY_TxPwrCtrlcckPwrOffset_bw40PwrOffsetEn_SHIFT);
			phy_reg_mod(pi, NPHY_TxPwrCtrlcckPwrOffset,
			NPHY_TxPwrCtrlcckPwrOffset_cckPwrOffsetEn_MASK,
			0x0 << NPHY_TxPwrCtrlcckPwrOffset_cckPwrOffsetEn_SHIFT);
		}
	} else if (NREV_IS(pi->pubpi.phy_rev, LCNXN_BASEREV + 5)) {
		if (CHSPEC_IS2G(pi->radio_chanspec)) {
			phy_reg_mod(pi, NPHY_TxPwrCtrlcckPwrOffset,
				NPHY_TxPwrCtrlcckPwrOffset_cckPwrOffsetEn_MASK,
				0x1 << NPHY_TxPwrCtrlcckPwrOffset_cckPwrOffsetEn_SHIFT);
			phy_reg_mod(pi, NPHY_TxPwrCtrlcckPwrOffset,
				NPHY_TxPwrCtrlcckPwrOffset_cckPwrOffset_MASK,
				0xD << NPHY_TxPwrCtrlcckPwrOffset_cckPwrOffset_SHIFT); /* 0.625dB */
			phy_reg_mod(pi, NPHY_TxPwrCtrlcckPwrOffset,
				NPHY_TxPwrCtrlcckPwrOffset_bw40PwrOffsetEn_MASK,
				0x0 << NPHY_TxPwrCtrlcckPwrOffset_bw40PwrOffsetEn_SHIFT);
		} else {
			/* 40MHz offset */
			phy_reg_mod(pi, NPHY_TxPwrCtrlNewFeatureEnables,
				NPHY_TxPwrCtrlNewFeatureEnables_bw40PwrOffset_MASK,
				4 << NPHY_TxPwrCtrlNewFeatureEnables_bw40PwrOffset_SHIFT);
			phy_reg_mod(pi, NPHY_TxPwrCtrlcckPwrOffset,
				NPHY_TxPwrCtrlcckPwrOffset_bw40PwrOffsetEn_MASK,
				0x1 << NPHY_TxPwrCtrlcckPwrOffset_bw40PwrOffsetEn_SHIFT);
			phy_reg_mod(pi, NPHY_TxPwrCtrlcckPwrOffset,
				NPHY_TxPwrCtrlcckPwrOffset_cckPwrOffsetEn_MASK,
				0x0 << NPHY_TxPwrCtrlcckPwrOffset_cckPwrOffsetEn_SHIFT);
		}
	}

	/* Now loading only for 1 power range only
	 * 4324 TBD other power range
	 */
	for (kk = 0; kk < 4; kk++) {
		phy_reg_mod(pi, NPHY_TxPwrCtrlNewFeatureEnables,
		NPHY_TxPwrCtrlNewFeatureEnables_estPwrDupMem_MASK,
		kk << NPHY_TxPwrCtrlNewFeatureEnables_estPwrDupMem_SHIFT);

		for (ctr = (kk << 6); ctr < ((kk << 6) + est_pwr_tbl_len); ctr++) {

			idx2tbl = ctr % 64;
			tssi = 2 * (ctr - 128);

			if (pi->sh->offset_targetpwr) {
				/* core 0 */
				pwr_est = wlc_phy_tssi2qtrdbm_neg_nphy(
					tssi, a1[0], b0[0], b1[0], 8);
				wlc_phy_table_write_nphy(pi, NPHY_TBL_ID_CORE1TXPWRCTL, 1,
					(idx2tbl + est_pwr_tbl_start_ind), 16, &pwr_est);
				/* core 1 */
				pwr_est = wlc_phy_tssi2qtrdbm_neg_nphy(
					tssi, a1[1], b0[1], b1[1], 8);
				wlc_phy_table_write_nphy(pi, NPHY_TBL_ID_CORE2TXPWRCTL, 1,
					(idx2tbl + est_pwr_tbl_start_ind), 16, &pwr_est);

			} else {
				/* core 0 */
				pwr_est = wlc_phy_tssi2qtrdbm_nphy(
					tssi, a1[0], b0[0], b1[0], 8);
				wlc_phy_table_write_nphy(pi, NPHY_TBL_ID_CORE1TXPWRCTL, 1,
					(idx2tbl + est_pwr_tbl_start_ind), 16, &pwr_est);
				/* core 1 */
				pwr_est = wlc_phy_tssi2qtrdbm_nphy(
					tssi, a1[1], b0[1], b1[1], 8);
				wlc_phy_table_write_nphy(pi, NPHY_TBL_ID_CORE2TXPWRCTL, 1,
					(idx2tbl + est_pwr_tbl_start_ind), 16, &pwr_est);
			}

#ifdef TWO_PWR_RANGE
			/* NEW 2nd estpwr Tbl */
			/* core 0 */
			pwr_est = wlc_phy_tssi2qtrdbm_nphy(
				tssi, a1lo[0], b0lo[0], b1lo[0], 8);
			phy_reg_mod(pi, NPHY_REV19_TxPwrCtrlNew1FeatureEnables,
				NPHY_REV19_TxPwrCtrlNew1FeatureEnables_estPwrSwitchLut_MASK,
				1 << NPHY_REV19_TxPwrCtrlNew1FeatureEnables_estPwrSwitchLut_SHIFT);
			wlc_phy_table_write_nphy(pi, NPHY_TBL_ID_CORE1TXPWRCTL, 1,
				(idx2tbl + est_pwr_tbl_start_ind), 16, &pwr_est);
			phy_reg_mod(pi, NPHY_REV19_TxPwrCtrlNew1FeatureEnables,
				NPHY_REV19_TxPwrCtrlNew1FeatureEnables_estPwrSwitchLut_MASK,
				0 << NPHY_REV19_TxPwrCtrlNew1FeatureEnables_estPwrSwitchLut_SHIFT);
			/* core 1 */
			pwr_est = wlc_phy_tssi2qtrdbm_nphy(
				tssi, a1lo[1], b0lo[1], b1lo[1], 8);
			phy_reg_mod(pi, NPHY_REV19_TxPwrCtrlNew1FeatureEnables,
				NPHY_REV19_TxPwrCtrlNew1FeatureEnables_estPwrSwitchLut_MASK,
				1 << NPHY_REV19_TxPwrCtrlNew1FeatureEnables_estPwrSwitchLut_SHIFT);
			wlc_phy_table_write_nphy(pi, NPHY_TBL_ID_CORE2TXPWRCTL, 1,
				(idx2tbl + est_pwr_tbl_start_ind), 16, &pwr_est);
			phy_reg_mod(pi, NPHY_REV19_TxPwrCtrlNew1FeatureEnables,
				NPHY_REV19_TxPwrCtrlNew1FeatureEnables_estPwrSwitchLut_MASK,
				0 << NPHY_REV19_TxPwrCtrlNew1FeatureEnables_estPwrSwitchLut_SHIFT);
#endif
		}
	}

#ifndef TWO_PWR_RANGE
	phy_reg_write(pi, NPHY_TxPwrCtrlIdleTssi_stg1_0,
		(uint16)(phy_reg_read(pi, NPHY_TxPwrCtrlIdleTssi0)));
	phy_reg_write(pi, NPHY_TxPwrCtrlIdleTssi_stg1_1,
		(uint16)(phy_reg_read(pi, NPHY_TxPwrCtrlIdleTssi1)));
#endif
}

/* core_code 3 and 4 forbidden for 4324 */
static void
wlc_phy_rev3_tssisel_nphy(phy_info_t *pi, uint8 core_code, uint8 rssi_type)
{
	uint8 cores = 0;
	/* core_codes
	 * CORESEL_CORE1: 001; CORESEL_CORE2: 010; CORESEL_CORE3: 011;
	 * CORESEL_CORE4: 100; CORESEL_ALLRX: 101; CORESEL_ALLTX: 110;
	 * CORESEL_ALLRXTX: 111
	 */
	if ((core_code == RADIO_MIMO_CORESEL_ALLRX) || (core_code == RADIO_MIMO_CORESEL_ALLTX) ||
		(core_code == RADIO_MIMO_CORESEL_ALLRXTX)) {
		cores = 3;
	} else {
		cores = core_code;
	}
	/* TBD: foreach core $cores */
	BCM_REFERENCE(cores);
	if ((rssi_type == NPHY_LCNXN_REV3_TSSI_OFF) || (core_code == RADIO_MIMO_CORESEL_OFF)) {
		wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 3,
			NPHY_TxPwrCtrlRfCtrlOverride0_amuxSelPortOverride_MASK,
			0x2, 0);
		if (PHY_IPA(pi)) {
		if (CHSPEC_IS5G(pi->radio_chanspec)) {
		wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 3,
			NPHY_TxPwrCtrlRfCtrlOverride0_tssiSelOverride_MASK,
			0, 0);
		wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 3,
			NPHY_TxPwrCtrlRfCtrlOverride0_tssiRangeOverride_MASK,
			1, 0);
		wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 3,
			NPHY_TxPwrCtrlRfCtrlOverride0_paCtrlTssiOverride_MASK,
			1, 0);
		wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 3,
			NPHY_TxPwrCtrlRfCtrlOverride1_afeAuxpgaSelVmidOverride_sel,
			0x7b, 0);
		wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 3,
			NPHY_TxPwrCtrlRfCtrlOverride1_afeAuxpgaSelGainOverride_sel,
			0x2, 0);
		} else {
		wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 3,
			NPHY_TxPwrCtrlRfCtrlOverride0_tssiSelOverride_MASK,
			0, 0);
		wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 3,
			NPHY_TxPwrCtrlRfCtrlOverride0_tssiRangeOverride_MASK,
			1, 0);
		wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 3,
			NPHY_TxPwrCtrlRfCtrlOverride0_paCtrlTssiOverride_MASK,
			1, 0);
		wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 3,
			NPHY_TxPwrCtrlRfCtrlOverride1_afeAuxpgaSelVmidOverride_sel,
			0xad, 0);
		wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 3,
			NPHY_TxPwrCtrlRfCtrlOverride1_afeAuxpgaSelGainOverride_sel,
			0x0, 0);
		}
		} else {
		wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 3,
			NPHY_TxPwrCtrlRfCtrlOverride0_tssiSelOverride_MASK,
			1, 0);
		wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 3,
			NPHY_TxPwrCtrlRfCtrlOverride0_tssiRangeOverride_MASK,
			1, 0);
		wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 3,
			NPHY_TxPwrCtrlRfCtrlOverride0_paCtrlTssiOverride_MASK,
			1, 0);
		wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 3,
			NPHY_TxPwrCtrlRfCtrlOverride1_afeAuxpgaSelVmidOverride_sel,
			0x9c, 0);
		wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 3,
			NPHY_TxPwrCtrlRfCtrlOverride1_afeAuxpgaSelGainOverride_sel,
			0x0, 0);
		}
		wlc_phy_rfctrl_override_nphy_rev19(pi,
			NPHY_REV19_RfctrlOverride_aux_en_MASK, 1, 3, 1,
			NPHY_REV19_RFCTRLOVERRIDE_ID4);
	} else {
		if (rssi_type == NPHY_RSSI_SEL_TBD) {
			PHY_INFORM(("\nIn fn wlc_phy_rev3_tssisel_nphy : Error!! TSSI tbd exit\n"));
		} else if (rssi_type == NPHY_RSSI_SEL_IQ) {
			wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 3,
				NPHY_TxPwrCtrlRfCtrlOverride0_amuxSelPortOverride_MASK,
				0x0, 1);
			wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 3,
				NPHY_TxPwrCtrlRfCtrlOverride0_tssiSelOverride_MASK,
				1, 1);
			wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 3,
				NPHY_TxPwrCtrlRfCtrlOverride0_tssiRangeOverride_MASK,
				1, 1);
			wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 3,
				NPHY_TxPwrCtrlRfCtrlOverride0_paCtrlTssiOverride_MASK,
				1, 1);
			if (NREV_IS(pi->pubpi.phy_rev, LCNXN_BASEREV + 3)) {
				wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 3,
					NPHY_TxPwrCtrlRfCtrlOverride1_afeAuxpgaSelVmidOverride_sel,
					0xa3, 1);
				wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 0,
					NPHY_TxPwrCtrlRfCtrlOverride1_afeAuxpgaSelVmidOverride_sel,
					0x9c, 1);
				wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 1,
					NPHY_TxPwrCtrlRfCtrlOverride1_afeAuxpgaSelVmidOverride_sel,
					0x9f, 1);
				wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 3,
					NPHY_TxPwrCtrlRfCtrlOverride1_afeAuxpgaSelGainOverride_sel,
					0x2, 1);
			} else {
				wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 1,
					NPHY_TxPwrCtrlRfCtrlOverride1_afeAuxpgaSelVmidOverride_sel,
					0xa3, 1);
				wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 1,
					NPHY_TxPwrCtrlRfCtrlOverride1_afeAuxpgaSelGainOverride_sel,
					0x2, 1);
				wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 0,
					NPHY_TxPwrCtrlRfCtrlOverride1_afeAuxpgaSelVmidOverride_sel,
					0x99, 1);
				wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 0,
					NPHY_TxPwrCtrlRfCtrlOverride1_afeAuxpgaSelGainOverride_sel,
					0x1, 1);
			}
			wlc_phy_rfctrl_override_nphy_rev19(pi,
				NPHY_REV19_RfctrlOverride_aux_en_MASK, 1, 3, 0,
				NPHY_REV19_RFCTRLOVERRIDE_ID4);
		} else if (rssi_type == NPHY_LCNXN_REV3_TSSI) {
			wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 3,
				NPHY_TxPwrCtrlRfCtrlOverride0_amuxSelPortOverride_MASK,
				0x2, 1);
			if (PHY_IPA(pi)) {
			if (CHSPEC_IS5G(pi->radio_chanspec)) {
			wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 3,
				NPHY_TxPwrCtrlRfCtrlOverride0_tssiSelOverride_MASK,
				0, 1);
			wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 3,
				NPHY_TxPwrCtrlRfCtrlOverride0_tssiRangeOverride_MASK,
				1, 1);
			wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 3,
				NPHY_TxPwrCtrlRfCtrlOverride0_paCtrlTssiOverride_MASK,
				1, 1);
			wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 3,
				NPHY_TxPwrCtrlRfCtrlOverride1_afeAuxpgaSelVmidOverride_sel,
				0x7b, 1);
			wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 3,
				NPHY_TxPwrCtrlRfCtrlOverride1_afeAuxpgaSelGainOverride_sel,
				0x2, 1);
			} else {
			wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 3,
				NPHY_TxPwrCtrlRfCtrlOverride0_tssiSelOverride_MASK,
				0, 1);
			wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 3,
				NPHY_TxPwrCtrlRfCtrlOverride0_tssiRangeOverride_MASK,
				1, 1);
			wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 3,
				NPHY_TxPwrCtrlRfCtrlOverride0_paCtrlTssiOverride_MASK,
				1, 1);
			wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 3,
				NPHY_TxPwrCtrlRfCtrlOverride1_afeAuxpgaSelVmidOverride_sel,
				0xad, 1);
			wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 3,
				NPHY_TxPwrCtrlRfCtrlOverride1_afeAuxpgaSelGainOverride_sel,
				0x0, 1);
			}
			} else {
			wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 3,
				NPHY_TxPwrCtrlRfCtrlOverride0_tssiSelOverride_MASK,
				1, 1);
			wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 3,
				NPHY_TxPwrCtrlRfCtrlOverride0_tssiRangeOverride_MASK,
				1, 1);
			wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 3,
				NPHY_TxPwrCtrlRfCtrlOverride0_paCtrlTssiOverride_MASK,
				1, 1);
			if (pi->sh->boardtype == 0x0670) {
				if (CHSPEC_IS2G(pi->radio_chanspec)) {
					wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 3,
					NPHY_TxPwrCtrlRfCtrlOverride1_afeAuxpgaSelVmidOverride_sel,
						0xB7, 1);
					wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 3,
					NPHY_TxPwrCtrlRfCtrlOverride1_afeAuxpgaSelGainOverride_sel,
						0x0, 1);
				} else if (CHSPEC_CHANNEL(pi->radio_chanspec) > 140) {
					wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 3,
					NPHY_TxPwrCtrlRfCtrlOverride1_afeAuxpgaSelVmidOverride_sel,
						0x80, 1);
					wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 3,
					NPHY_TxPwrCtrlRfCtrlOverride1_afeAuxpgaSelGainOverride_sel,
						0x2, 1);
				} else {
					/* 5G channels till ch 140 */
					wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 3,
					NPHY_TxPwrCtrlRfCtrlOverride1_afeAuxpgaSelVmidOverride_sel,
						0x5C, 1);
					wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 3,
					NPHY_TxPwrCtrlRfCtrlOverride1_afeAuxpgaSelGainOverride_sel,
						0x4, 1);
				}
			} else {
				wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 3,
					NPHY_TxPwrCtrlRfCtrlOverride1_afeAuxpgaSelVmidOverride_sel,
					0x9c, 1);
				wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 3,
					NPHY_TxPwrCtrlRfCtrlOverride1_afeAuxpgaSelGainOverride_sel,
					0x0, 1);
			}
			}
			wlc_phy_rfctrl_override_nphy_rev19(pi,
				NPHY_REV19_RfctrlOverride_aux_en_MASK, 1, 3, 0,
				NPHY_REV19_RFCTRLOVERRIDE_ID4);
		} else if (rssi_type == NPHY_LCNXN_REV3_TSSI2) {
			wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 3,
				NPHY_TxPwrCtrlRfCtrlOverride0_amuxSelPortOverride_MASK,
				0x2, 1);
			if (PHY_IPA(pi)) {
			if (CHSPEC_IS5G(pi->radio_chanspec)) {
			wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 3,
				NPHY_TxPwrCtrlRfCtrlOverride0_tssiSelOverride_MASK,
				0, 1);
			wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 3,
				NPHY_TxPwrCtrlRfCtrlOverride0_tssiRangeOverride_MASK,
				0, 1);
			wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 3,
				NPHY_TxPwrCtrlRfCtrlOverride0_paCtrlTssiOverride_MASK,
				1, 1);
			wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 3,
				NPHY_TxPwrCtrlRfCtrlOverride1_afeAuxpgaSelVmidOverride_sel,
				0x8f, 1);
			wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 3,
				NPHY_TxPwrCtrlRfCtrlOverride1_afeAuxpgaSelGainOverride_sel,
				0x2, 1);
			} else {
			wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 3,
				NPHY_TxPwrCtrlRfCtrlOverride0_tssiSelOverride_MASK,
				0, 1);
			wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 3,
				NPHY_TxPwrCtrlRfCtrlOverride0_tssiRangeOverride_MASK,
				0, 1);
			wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 3,
				NPHY_TxPwrCtrlRfCtrlOverride0_paCtrlTssiOverride_MASK,
				1, 1);
			wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 3,
				NPHY_TxPwrCtrlRfCtrlOverride1_afeAuxpgaSelVmidOverride_sel,
				0x7e, 1);
			wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 3,
				NPHY_TxPwrCtrlRfCtrlOverride1_afeAuxpgaSelGainOverride_sel,
				0x3, 1);
			}
			}
			wlc_phy_rfctrl_override_nphy_rev19(pi,
				NPHY_REV19_RfctrlOverride_aux_en_MASK, 1, 3, 0,
				NPHY_REV19_RFCTRLOVERRIDE_ID4);
		} else if (rssi_type == NPHY_LCNXN_REV3_TSSI_FOR_IQ) {
			wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 3,
				NPHY_TxPwrCtrlRfCtrlOverride0_amuxSelPortOverride_MASK,
				0x2, 1);
			wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 3,
				NPHY_TxPwrCtrlRfCtrlOverride0_tssiSelOverride_MASK,
				1, 1);
			wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 3,
				NPHY_TxPwrCtrlRfCtrlOverride0_tssiRangeOverride_MASK,
				1, 1);
			wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 3,
				NPHY_TxPwrCtrlRfCtrlOverride0_paCtrlTssiOverride_MASK,
				1, 1);
			wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 3,
				NPHY_TxPwrCtrlRfCtrlOverride1_afeAuxpgaSelVmidOverride_sel,
				0x33, 1);
			wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 3,
				NPHY_TxPwrCtrlRfCtrlOverride1_afeAuxpgaSelGainOverride_sel,
				0x7, 1);
			wlc_phy_rfctrl_override_nphy_rev19(pi,
				NPHY_REV19_RfctrlOverride_aux_en_MASK, 1, 3, 0,
				NPHY_REV19_RFCTRLOVERRIDE_ID4);

		} else {
			PHY_INFORM(("\nIn fn wlc_phy_rev3_tssisel_nphy : Error!!"));
			PHY_INFORM(("\nTSSI is $rssi_type exit\n"));
		}
	}
}

static void
wlc_phy_rev3_setup_rfiqcal_mux(phy_info_t *pi, uint16 attenuator, bool override, uint8 tssi0_iqcal1)
{
	uint8 ext_env_det = 0;
	phy_info_nphy_t *pi_nphy;
	pi_nphy = (phy_info_nphy_t *)pi->u.pi_nphy;
	if (pi_nphy == NULL)
		return;

	if ((PHY_IPA(pi)) && ((tssi0_iqcal1 & 1) == 0)) {
		ext_env_det = 0;
	} else if ((tssi0_iqcal1 == 1) && (pi_nphy->nphy_use_int_tx_iqlo_cal == 1)) {
		ext_env_det = 0;
	} else if (tssi0_iqcal1 == 2) {
		ext_env_det = 0;
	} else {
		ext_env_det = 1;
	}

	/* In case of iboard set the mux to internal TSSI */
	if (pi->sh->boardtype == 0x0670) {
		ext_env_det = 0;
	}

	if (override) {
		wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 3,
			NPHY_TxPwrCtrlRfCtrlOverride0_tssiRangeOverride_MASK,
			attenuator, 1);
		wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, 3,
			NPHY_TxPwrCtrlRfCtrlOverride0_tssiSelOverride_MASK,
			1, 1);
		if (tssi0_iqcal1 == 1) {
			wlc_phy_rev3_tssisel_nphy(pi, 5, NPHY_RSSI_SEL_IQ);
		} else if (tssi0_iqcal1 == 2) {
			wlc_phy_rev3_tssisel_nphy(pi, 5, NPHY_LCNXN_REV3_TSSI_FOR_IQ);
		} else if (tssi0_iqcal1 == 4) {
			wlc_phy_rev3_tssisel_nphy(pi, 5, NPHY_LCNXN_REV3_TSSI2);
		} else {
			wlc_phy_rev3_tssisel_nphy(pi, 5, NPHY_LCNXN_REV3_TSSI);
		}
	}
	/* save all radio regs which are going to be changed */
	pi_nphy->tx_rx_cal_radio_saveregs_rev19[0] =
		read_radio_reg(pi, RADIO_20671_IQCAL_CFG1_CORE0);
	pi_nphy->tx_rx_cal_radio_saveregs_rev19[1] =
		read_radio_reg(pi, RADIO_20671_IQCAL_CFG1_CORE1);
	pi_nphy->tx_rx_radio_reg_save = 1;

	if (CHSPEC_IS5G(pi->radio_chanspec)) {
		/* Enable TSSI ENV Det PAD A-band mux */
		mod_radio_reg(pi, RADIO_20671_IQCAL_CFG1_CORE0, 0xf0,
			((2 + ((tssi0_iqcal1 & 0x1) << 3) + ext_env_det) << 4));
		mod_radio_reg(pi, RADIO_20671_IQCAL_CFG1_CORE1, 0xf0,
			((2 + ((tssi0_iqcal1 & 0x1) << 3) + ext_env_det) << 4));
	} else {
		/* Enable TSSI ENV Det PAD G-band mux */
		mod_radio_reg(pi, RADIO_20671_IQCAL_CFG1_CORE0, 0xf0,
			(((tssi0_iqcal1 & 0x1) << 3) + ext_env_det) << 4);
		mod_radio_reg(pi, RADIO_20671_IQCAL_CFG1_CORE1, 0xf0,
			((((tssi0_iqcal1 & 0x1) << 3) + ext_env_det) << 4));
	}
	mod_radio_reg(pi, RADIO_20671_IQCAL_CFG1_CORE0, 0x4,
		ext_env_det << 2);
	mod_radio_reg(pi, RADIO_20671_IQCAL_CFG1_CORE1, 0x4,
		ext_env_det << 2);

	/* PU_iqcal_core0/1 */
	 if (tssi0_iqcal1 == 1) {
		mod_radio_reg(pi, RADIO_20671_IQCAL_CFG1_CORE0, 0x2,
			1 << 1);
		mod_radio_reg(pi, RADIO_20671_IQCAL_CFG1_CORE1, 0x2,
			1 << 1);

	}
}

/* default args to pass : core = 0, override_val = 0, ovr = 1 */
static void
wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(phy_info_t *pi, uint8 cores, uint16 en_field,
	uint16 override_val, uint16 ovr)
{
	uint8 core = cores;
	uint16 field = en_field;
	uint16 ovr_val = override_val;
	uint16 override = ovr;
	bool flag = 0;
	uint16 en_addr = 0xffff;
	uint16 val_addr = 0xffff;

	if (core == 3) {
		core = 1;
		wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy(pi, core, field, ovr_val,
			override);
		core = 0;
	}

	en_addr = (core == 0) ? NPHY_TxPwrCtrlRfCtrlOverride0_core0:
		NPHY_TxPwrCtrlRfCtrlOverride0_core1;
	val_addr = (core == 0) ? NPHY_TxPwrCtrlRfCtrlOverride0_core0:
		NPHY_TxPwrCtrlRfCtrlOverride0_core1;

	if ((field == NPHY_TxPwrCtrlRfCtrlOverride1_afeAuxpgaSelVmidOverride_sel) ||
		(field == NPHY_TxPwrCtrlRfCtrlOverride1_afeAuxpgaSelGainOverride_sel)) {
		flag = 1;
	}

	if (flag == 0) {
	switch (field) {
		case NPHY_TxPwrCtrlRfCtrlOverride0_tssiRangeOverride_MASK:
		phy_reg_mod(pi, en_addr,
			NPHY_TxPwrCtrlRfCtrlOverride0_tssiRangeOverrideVal_MASK,
			ovr_val << NPHY_TxPwrCtrlRfCtrlOverride0_tssiRangeOverrideVal_SHIFT);
		phy_reg_mod(pi, en_addr,
			NPHY_TxPwrCtrlRfCtrlOverride0_tssiRangeOverride_MASK,
			override << NPHY_TxPwrCtrlRfCtrlOverride0_tssiRangeOverride_SHIFT);
		break;
		case NPHY_TxPwrCtrlRfCtrlOverride0_tssiSelOverride_MASK:
		phy_reg_mod(pi, en_addr,
			NPHY_TxPwrCtrlRfCtrlOverride0_tssiSelOverrideVal_MASK,
			ovr_val << NPHY_TxPwrCtrlRfCtrlOverride0_tssiSelOverrideVal_SHIFT);
		phy_reg_mod(pi, en_addr,
			NPHY_TxPwrCtrlRfCtrlOverride0_tssiSelOverride_MASK,
			override << NPHY_TxPwrCtrlRfCtrlOverride0_tssiSelOverride_SHIFT);
		break;
		case NPHY_TxPwrCtrlRfCtrlOverride0_paCtrlTssiOverride_MASK:
		phy_reg_mod(pi, en_addr,
			NPHY_TxPwrCtrlRfCtrlOverride0_paCtrlTssiOverrideVal_MASK,
			ovr_val << NPHY_TxPwrCtrlRfCtrlOverride0_paCtrlTssiOverrideVal_SHIFT);
		phy_reg_mod(pi, en_addr,
			NPHY_TxPwrCtrlRfCtrlOverride0_paCtrlTssiOverride_MASK,
			override << NPHY_TxPwrCtrlRfCtrlOverride0_paCtrlTssiOverride_SHIFT);
		break;
		case NPHY_TxPwrCtrlRfCtrlOverride0_amuxSelPortOverride_MASK:
		phy_reg_mod(pi, en_addr,
			NPHY_TxPwrCtrlRfCtrlOverride0_amuxSelPortOverrideVal_MASK,
			ovr_val << NPHY_TxPwrCtrlRfCtrlOverride0_amuxSelPortOverrideVal_SHIFT);
		phy_reg_mod(pi, en_addr,
			NPHY_TxPwrCtrlRfCtrlOverride0_amuxSelPortOverride_MASK,
			override << NPHY_TxPwrCtrlRfCtrlOverride0_amuxSelPortOverride_SHIFT);
		break;
		case NPHY_TxPwrCtrlRfCtrlOverride0_tempsenseSwapOverride_MASK:
		phy_reg_mod(pi, en_addr,
			NPHY_TxPwrCtrlRfCtrlOverride0_tempsenseSwapOverrideVal_MASK,
			ovr_val << NPHY_TxPwrCtrlRfCtrlOverride0_tempsenseSwapOverrideVal_SHIFT);
		phy_reg_mod(pi, en_addr,
			NPHY_TxPwrCtrlRfCtrlOverride0_tempsenseSwapOverride_MASK,
			override << NPHY_TxPwrCtrlRfCtrlOverride0_tempsenseSwapOverride_SHIFT);
		break;
		case NPHY_TxPwrCtrlRfCtrlOverride0_tempCompSwapOverride_MASK:
		phy_reg_mod(pi, val_addr,
			NPHY_TxPwrCtrlRfCtrlOverride1_tempCompSwapOverrideVal_MASK,
			ovr_val << NPHY_TxPwrCtrlRfCtrlOverride1_tempCompSwapOverrideVal_SHIFT);
		phy_reg_mod(pi, en_addr,
			NPHY_TxPwrCtrlRfCtrlOverride0_tempCompSwapOverride_MASK,
			override << NPHY_TxPwrCtrlRfCtrlOverride0_tempCompSwapOverride_SHIFT);
		break;
		default:
			PHY_ERROR(("\nIn fn wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy"));
			PHY_ERROR(("\nNo such field\n"));
		break;
	} /* End of switch case 1 */
	} else if (flag == 1) {
		en_addr = (core == 0) ? NPHY_TxPwrCtrlRfCtrlOverride1_core0:
			NPHY_TxPwrCtrlRfCtrlOverride1_core1;
		val_addr = (core == 0) ? NPHY_TxPwrCtrlRfCtrlOverride1_core0:
			NPHY_TxPwrCtrlRfCtrlOverride1_core1;
	switch (field) {
		case NPHY_TxPwrCtrlRfCtrlOverride1_afeAuxpgaSelVmidOverride_sel:
		phy_reg_mod(pi, val_addr,
			NPHY_TxPwrCtrlRfCtrlOverride1_afeAuxpgaSelVmidOverrideVal_MASK,
			ovr_val << NPHY_TxPwrCtrlRfCtrlOverride1_afeAuxpgaSelVmidOverrideVal_SHIFT);
		phy_reg_mod(pi, val_addr,
			NPHY_TxPwrCtrlRfCtrlOverride1_afeAuxpgaSelVmidOverride_MASK,
			override << NPHY_TxPwrCtrlRfCtrlOverride1_afeAuxpgaSelVmidOverride_SHIFT);
		break;
		case NPHY_TxPwrCtrlRfCtrlOverride1_afeAuxpgaSelGainOverride_sel:
		phy_reg_mod(pi, val_addr,
			NPHY_TxPwrCtrlRfCtrlOverride1_afeAuxpgaSelGainOverrideVal_MASK,
			ovr_val << NPHY_TxPwrCtrlRfCtrlOverride1_afeAuxpgaSelGainOverrideVal_SHIFT);
		phy_reg_mod(pi, val_addr,
			NPHY_TxPwrCtrlRfCtrlOverride1_afeAuxpgaSelGainOverride_MASK,
			override << NPHY_TxPwrCtrlRfCtrlOverride1_afeAuxpgaSelGainOverride_SHIFT);
		break;
		default:
			PHY_ERROR(("\nIn fn wlc_phy_rev3_pwrctrl_rfctrl_override_signals_nphy"));
			PHY_ERROR(("\nNo such field\n"));
		break;
	} /* End of switch case 2 */
	}
}

/* Tx Power Control FSM Setup the table values 4324
 * FIXME4324 : To set up gain table mode 1 or 0?
 */

/* default args to pass :
 * tx_pwr_ctrl_scheme = 0; gn_tbl_mode = 1; vbat_fsm_dis = 1
 */
static void
wlc_phy_lcnxn_rev3_setup_pwr_ctrl_mux_tables_nphy(phy_info_t *pi, uint8 tx_pwr_ctrl_scheme,
	uint8 gn_tbl_mode, uint8 vbat_fsm_dis)
{
	/* ----------------------------------------------
	 * if max_target_pwr among all rates is M	*
	 * minpwr = (2M - pwr_range_upper_limit) << 2	*
	 * maxpwr = (2M - pwr_range_lower_limit) << 2	*
	 * ----------------------------------------------
	 */

	uint16 minpwr = 92;  /* (2*16.5 - 10) * 4 */
	uint16 maxpwr = 120; /* (2*16.5 - 03) * 4 */

	/* gn_tbl_mode = 0 for 0.25 dB steps; 1 for 0.5 dB steps */
	if (PHY_IPA(pi)) {
		/* IPA currently hard code to 0.5 dB steps */
		phy_reg_mod(pi, NPHY_TxPwrCtrlNewFeatureEnables,
			NPHY_TxPwrCtrlNewFeatureEnables_txGainTable_mode_MASK,
			1 << NPHY_TxPwrCtrlNewFeatureEnables_txGainTable_mode_SHIFT);
	} else {
		phy_reg_mod(pi, NPHY_TxPwrCtrlNewFeatureEnables,
			NPHY_TxPwrCtrlNewFeatureEnables_txGainTable_mode_MASK,
			gn_tbl_mode << NPHY_TxPwrCtrlNewFeatureEnables_txGainTable_mode_SHIFT);
	}
	/*  Set 2 for 2 power range */
	phy_reg_mod(pi, NPHY_REV19_TxPwrCtrlNew1FeatureEnables,
		NPHY_REV19_TxPwrCtrlNew1FeatureEnables_txPwrCtrlScheme_MASK,
		tx_pwr_ctrl_scheme <<
		NPHY_REV19_TxPwrCtrlNew1FeatureEnables_txPwrCtrlScheme_SHIFT);
	/* Disable the V-BAT tempsense FSM */
	phy_reg_mod(pi, NPHY_REV19_TxPwrCtrlNew1FeatureEnables,
		NPHY_REV19_TxPwrCtrlNew1FeatureEnables_vbat_tempsense_disable_MASK,
		vbat_fsm_dis <<
		NPHY_REV19_TxPwrCtrlNew1FeatureEnables_vbat_tempsense_disable_SHIFT);
	if (PHY_IPA(pi)) {
		/* PAD output or something else (PA). Need to make sure this is the MUX */
		wlc_phy_lcnxn_rev3_pwrctrl_rfctrl_reg_nphy(pi, 3,
			NPHY_REV19_TxPwrCtrlRfCtrl0_tssiSelVal0_MASK,
			0x0);
		wlc_phy_lcnxn_rev3_pwrctrl_rfctrl_reg_nphy(pi, 3,
			NPHY_REV19_TxPwrCtrlRfCtrl0_tssiSelVal1_MASK,
			0x0);
		/* PA output attenuated or not -> Value is useless if tssiSelVal is 1 */
		wlc_phy_lcnxn_rev3_pwrctrl_rfctrl_reg_nphy(pi, 3,
			NPHY_REV19_TxPwrCtrlRfCtrl0_tssiRangeVal0_MASK,
			0x1);
#ifdef TWO_PWR_RANGE
		wlc_phy_lcnxn_rev3_pwrctrl_rfctrl_reg_nphy(pi, 3,
			NPHY_REV19_TxPwrCtrlRfCtrl0_tssiRangeVal1_MASK,
			0x0);
#else
		wlc_phy_lcnxn_rev3_pwrctrl_rfctrl_reg_nphy(pi, 3,
			NPHY_REV19_TxPwrCtrlRfCtrl0_tssiRangeVal1_MASK,
			0x1);
#endif
	} else {
		/* PAD output or something else (PA). Need to make sure this is the MUX */
		wlc_phy_lcnxn_rev3_pwrctrl_rfctrl_reg_nphy(pi, 3,
			NPHY_REV19_TxPwrCtrlRfCtrl0_tssiSelVal0_MASK,
			0x1);
		wlc_phy_lcnxn_rev3_pwrctrl_rfctrl_reg_nphy(pi, 3,
			NPHY_REV19_TxPwrCtrlRfCtrl0_tssiSelVal1_MASK,
			0x1);
		/* PA output attenuated or not -> Value is useless if tssiSelVal is 1 */
		wlc_phy_lcnxn_rev3_pwrctrl_rfctrl_reg_nphy(pi, 3,
			NPHY_REV19_TxPwrCtrlRfCtrl0_tssiRangeVal0_MASK,
			0x1);
		wlc_phy_lcnxn_rev3_pwrctrl_rfctrl_reg_nphy(pi, 3,
			NPHY_REV19_TxPwrCtrlRfCtrl0_tssiRangeVal1_MASK,
			0x1);
	}
	/* amuxSelPort */
	wlc_phy_lcnxn_rev3_pwrctrl_rfctrl_reg_nphy(pi, 3,
		NPHY_REV19_TxPwrCtrlRfCtrl0_amuxSelPortVal0_MASK,
		0x2);
	wlc_phy_lcnxn_rev3_pwrctrl_rfctrl_reg_nphy(pi, 3,
		NPHY_REV19_TxPwrCtrlRfCtrl0_amuxSelPortVal1_MASK,
		0x1);
	wlc_phy_lcnxn_rev3_pwrctrl_rfctrl_reg_nphy(pi, 3,
		NPHY_REV19_TxPwrCtrlRfCtrl0_amuxSelPortVal2_MASK,
		0x1);
	/* pa_ctrl_tssi : To be tuned */
	wlc_phy_lcnxn_rev3_pwrctrl_rfctrl_reg_nphy(pi, 3,
		NPHY_REV19_TxPwrCtrlRfCtrl1_paCtrlTssiVal0_MASK,
		0x1);
	wlc_phy_lcnxn_rev3_pwrctrl_rfctrl_reg_nphy(pi, 3,
		NPHY_REV19_TxPwrCtrlRfCtrl1_paCtrlTssiVal1_MASK,
		0x1);
	wlc_phy_lcnxn_rev3_pwrctrl_rfctrl_reg_nphy(pi, 3,
		NPHY_REV19_TxPwrCtrlRfCtrl1_paCtrlTssiVal2_MASK,
		0x1);
	/* Tempsense Tempcomp: Not used curently */
	wlc_phy_lcnxn_rev3_pwrctrl_rfctrl_reg_nphy(pi, 3,
		NPHY_REV19_TxPwrCtrlRfCtrl1_tempsenseSwapVal0_MASK,
		0x0);
	wlc_phy_lcnxn_rev3_pwrctrl_rfctrl_reg_nphy(pi, 3,
		NPHY_REV19_TxPwrCtrlRfCtrl1_tempsenseSwapVal1_MASK,
		0x1);
	wlc_phy_lcnxn_rev3_pwrctrl_rfctrl_reg_nphy(pi, 3,
		NPHY_REV19_TxPwrCtrlRfCtrl1_tempCompSwapVal0_MASK,
		0x0);
	wlc_phy_lcnxn_rev3_pwrctrl_rfctrl_reg_nphy(pi, 3,
		NPHY_REV19_TxPwrCtrlRfCtrl1_tempCompSwapVal1_MASK,
		0x1);
	/* Aux PGA settings for Vmid and Gain: To be tuned */
	if (PHY_IPA(pi)) {
		if (CHSPEC_IS2G(pi->radio_chanspec)) {
		wlc_phy_lcnxn_rev3_pwrctrl_rfctrl_reg_nphy(pi, 3, NPHY_field_afe_aux_pga_vmid_val0,
			0xad);
		wlc_phy_lcnxn_rev3_pwrctrl_rfctrl_reg_nphy(pi, 3, NPHY_field_afe_aux_pga_vmid_val1,
			0xad);
#ifdef TWO_PWR_RANGE
		wlc_phy_lcnxn_rev3_pwrctrl_rfctrl_reg_nphy(pi, 3, NPHY_field_afe_aux_pga_vmid_val2,
			0x7e);
#else
		wlc_phy_lcnxn_rev3_pwrctrl_rfctrl_reg_nphy(pi, 3, NPHY_field_afe_aux_pga_vmid_val2,
			0xad);
#endif
		wlc_phy_lcnxn_rev3_pwrctrl_rfctrl_reg_nphy(pi, 3, NPHY_field_afe_aux_pga_vmid_val3,
			0xad);
		wlc_phy_lcnxn_rev3_pwrctrl_rfctrl_reg_nphy(pi, 3, NPHY_field_afe_aux_pga_vmid_val4,
			0x9d);
		wlc_phy_lcnxn_rev3_pwrctrl_rfctrl_reg_nphy(pi, 3, NPHY_field_afe_aux_pga_gain_val0,
			0x0);
		wlc_phy_lcnxn_rev3_pwrctrl_rfctrl_reg_nphy(pi, 3, NPHY_field_afe_aux_pga_gain_val1,
			0x0);
#ifdef TWO_PWR_RANGE
		wlc_phy_lcnxn_rev3_pwrctrl_rfctrl_reg_nphy(pi, 3, NPHY_field_afe_aux_pga_gain_val2,
			0x3);
#else
		wlc_phy_lcnxn_rev3_pwrctrl_rfctrl_reg_nphy(pi, 3, NPHY_field_afe_aux_pga_gain_val2,
			0x0);
#endif
		wlc_phy_lcnxn_rev3_pwrctrl_rfctrl_reg_nphy(pi, 3, NPHY_field_afe_aux_pga_gain_val3,
			0x0);
		wlc_phy_lcnxn_rev3_pwrctrl_rfctrl_reg_nphy(pi, 3, NPHY_field_afe_aux_pga_gain_val4,
			0x3);
		} else {
		wlc_phy_lcnxn_rev3_pwrctrl_rfctrl_reg_nphy(pi, 3, NPHY_field_afe_aux_pga_vmid_val0,
			0x7b);
		wlc_phy_lcnxn_rev3_pwrctrl_rfctrl_reg_nphy(pi, 3, NPHY_field_afe_aux_pga_vmid_val1,
			0x7b);
#ifdef TWO_PWR_RANGE
		wlc_phy_lcnxn_rev3_pwrctrl_rfctrl_reg_nphy(pi, 3, NPHY_field_afe_aux_pga_vmid_val2,
			0x8f);
#else
		wlc_phy_lcnxn_rev3_pwrctrl_rfctrl_reg_nphy(pi, 3, NPHY_field_afe_aux_pga_vmid_val2,
			0x7b);
#endif
		wlc_phy_lcnxn_rev3_pwrctrl_rfctrl_reg_nphy(pi, 3, NPHY_field_afe_aux_pga_vmid_val3,
			0x7b);
		wlc_phy_lcnxn_rev3_pwrctrl_rfctrl_reg_nphy(pi, 3, NPHY_field_afe_aux_pga_vmid_val4,
			0x9d);
		wlc_phy_lcnxn_rev3_pwrctrl_rfctrl_reg_nphy(pi, 3, NPHY_field_afe_aux_pga_gain_val0,
			0x2);
		wlc_phy_lcnxn_rev3_pwrctrl_rfctrl_reg_nphy(pi, 3, NPHY_field_afe_aux_pga_gain_val1,
			0x2);
#ifdef TWO_PWR_RANGE
		wlc_phy_lcnxn_rev3_pwrctrl_rfctrl_reg_nphy(pi, 3, NPHY_field_afe_aux_pga_gain_val2,
			0x2);
#else
		wlc_phy_lcnxn_rev3_pwrctrl_rfctrl_reg_nphy(pi, 3, NPHY_field_afe_aux_pga_gain_val2,
			0x2);
#endif
		wlc_phy_lcnxn_rev3_pwrctrl_rfctrl_reg_nphy(pi, 3, NPHY_field_afe_aux_pga_gain_val3,
			0x2);
		wlc_phy_lcnxn_rev3_pwrctrl_rfctrl_reg_nphy(pi, 3, NPHY_field_afe_aux_pga_gain_val4,
			0x3);
		}
	} else {
		wlc_phy_lcnxn_rev3_pwrctrl_rfctrl_reg_nphy(pi, 3, NPHY_field_afe_aux_pga_vmid_val0,
			0x9c);
		wlc_phy_lcnxn_rev3_pwrctrl_rfctrl_reg_nphy(pi, 3, NPHY_field_afe_aux_pga_vmid_val1,
			0x9c);
		wlc_phy_lcnxn_rev3_pwrctrl_rfctrl_reg_nphy(pi, 3, NPHY_field_afe_aux_pga_vmid_val2,
			0x9c);
		wlc_phy_lcnxn_rev3_pwrctrl_rfctrl_reg_nphy(pi, 3, NPHY_field_afe_aux_pga_vmid_val3,
			0x9c);
		wlc_phy_lcnxn_rev3_pwrctrl_rfctrl_reg_nphy(pi, 3, NPHY_field_afe_aux_pga_vmid_val4,
			0x9d);
		wlc_phy_lcnxn_rev3_pwrctrl_rfctrl_reg_nphy(pi, 3, NPHY_field_afe_aux_pga_gain_val0,
			0x0);
		wlc_phy_lcnxn_rev3_pwrctrl_rfctrl_reg_nphy(pi, 3, NPHY_field_afe_aux_pga_gain_val1,
			0x0);
		wlc_phy_lcnxn_rev3_pwrctrl_rfctrl_reg_nphy(pi, 3, NPHY_field_afe_aux_pga_gain_val2,
			0x0);
		wlc_phy_lcnxn_rev3_pwrctrl_rfctrl_reg_nphy(pi, 3, NPHY_field_afe_aux_pga_gain_val3,
			0x0);
		wlc_phy_lcnxn_rev3_pwrctrl_rfctrl_reg_nphy(pi, 3, NPHY_field_afe_aux_pga_gain_val4,
			0x3);
	}

	phy_reg_mod(pi, NPHY_TxPwrCtrlPwrRange2_core0,
		NPHY_TxPwrCtrlPwrRange2_pwrMin_range2_MASK,
		(minpwr) << NPHY_TxPwrCtrlPwrRange2_pwrMin_range2_SHIFT);
	phy_reg_mod(pi, NPHY_TxPwrCtrlPwrRange2_core0,
		NPHY_TxPwrCtrlPwrRange2_pwrMax_range2_MASK,
		(maxpwr) << NPHY_TxPwrCtrlPwrRange2_pwrMax_range2_SHIFT);
	phy_reg_mod(pi, NPHY_TxPwrCtrlPwrRange2_core1,
		NPHY_TxPwrCtrlPwrRange2_pwrMin_range2_MASK,
		(minpwr) << NPHY_TxPwrCtrlPwrRange2_pwrMin_range2_SHIFT);
	phy_reg_mod(pi, NPHY_TxPwrCtrlPwrRange2_core1,
		NPHY_TxPwrCtrlPwrRange2_pwrMax_range2_MASK,
		(maxpwr) << NPHY_TxPwrCtrlPwrRange2_pwrMax_range2_SHIFT);
}

