#undef SPINWAIT
#define SPINWAIT(exp, us) { \
	uint countdown = (us) + 9; \
	while ((exp) && (countdown >= 10)) {\
			OSL_YIELD_DELAY(10); \
			countdown -= 10; \
	} \
}
