#if defined(WLC_LOW) && defined(BCMPKTPOOL) && defined(DMATXRC)
int
BCMATTACHFN(wlc_phdr_attach)(wlc_info_t *wlc)
{
	/* This happens before reclaim so
	 * start with 2 then fill later
	 */
	int n = 2;
	int err;

	wlc->phdr_len = PHDR_POOL_LEN;
	if ((wlc->phdr_list = MALLOC(wlc->osh, (wlc->phdr_len * sizeof(void *)))) == NULL) {
		WL_ERROR(("wl%d: %s: failed to allocate peek list\n",
			wlc->pub->unit, __FUNCTION__));
		err = BCME_ERROR;
		goto exit;
	}
	bzero(wlc->phdr_list, (wlc->phdr_len * sizeof(void *)));

	if ((wlc->pktpool_phdr = MALLOC(wlc->osh, sizeof(pktpool_t))) == NULL) {
		WL_ERROR(("wl%d: %s: failed to allocate phdr\n",
			wlc->pub->unit, __FUNCTION__));
		err = BCME_ERROR;
		goto exit;
	}
	bzero(wlc->pktpool_phdr, sizeof(pktpool_t));

	err = pktpool_init(wlc->osh, PHDR_POOL(wlc), &n, TXOFF, TRUE);
	if ((err == 0) && (n < 2))
		WL_ERROR(("partial hdrs pkt pool allocated: %d %d\n", n, 2));
exit:
	return err;
}

void
BCMATTACHFN(wlc_phdr_detach)(wlc_info_t *wlc)
{
	if (wlc->phdr_list) {
		MFREE(wlc->osh, wlc->phdr_list, (wlc->phdr_len * sizeof(void *)));
		wlc->phdr_list = NULL;
	}

	if (PHDR_POOL(wlc)) {
		pktpool_deinit(wlc->osh, PHDR_POOL(wlc));

		MFREE(wlc->osh, PHDR_POOL(wlc), sizeof(pktpool_t));
		wlc->pktpool_phdr = NULL;
	}
}

static void*
wlc_phdr_get(wlc_info_t *wlc)
{
	return pktpool_get(PHDR_POOL(wlc));
}

/* Incrementally add to pkt header pool when memory
 * is available after reclaim
 */
void
wlc_phdr_fill(wlc_info_t* wlc)
{
	osl_t *osh = wlc->osh;
	void *p;
	int len;

	len = pktpool_len(PHDR_POOL(wlc));
	for (; len < wlc->phdr_len; len++) {
		p = PKTGET(osh, TXOFF, TRUE);
		if (p == NULL)
			break;

		if (pktpool_add(PHDR_POOL(wlc), p) != BCME_OK) {
			PKTFREE(osh, p, FALSE);
			break;
		}
	}
}
#endif /* WLC_LOW && BCMPKTPOOL && DMATXRC */

