#ifdef PROP_TXSTATUS
	/* Return TRUE if the packet should be wlc_suppressed */
bool
wlc_suppress_sync_fsm(wlc_info_t *wlc, struct scb* sc, void* p, bool suppress)
{
	uint8 rc = FALSE;

	if (!HOST_PROPTXSTATUS_ACTIVATED(wlc))
	return FALSE;

	if (WLPKTTAG(p)->flags & WLF_8021X)
		return FALSE;

	if (SCB_PROPTXTSTATUS_SUPPR_STATE(sc)) {
		/* "suppress" state */
		if (!suppress && WLFC_PKTID_GEN(WLPKTTAG(p)->wl_hdr_information) ==
			SCB_PROPTXTSTATUS_SUPPR_GEN(sc)) {
			/* now the host is in sync, since the generation is a match */
			SCB_PROPTXTSTATUS_SUPPR_SETSTATE(sc, 0);
			sc->first_sup_pkt = 0xffff;
		}
		else if (sc->first_sup_pkt == (WLPKTTAG(p)->wl_hdr_information & 0xff)) {
			SCB_PROPTXTSTATUS_SUPPR_SETGEN(sc, (SCB_PROPTXTSTATUS_SUPPR_GEN(sc) ^ 1));
		}
		else {
			rc = TRUE;
		}
	}
	else {
		/* "accept" state */
		if (suppress && (WL_TXSTATUS_GET_FLAGS(WLPKTTAG(p)->wl_hdr_information) &
			WLFC_PKTFLAG_PKTFROMHOST)) {
			/* A suppress occurred while in "accept" state;
			 * switch state, update gen # ONLY when packet is from HOST
			 */
			SCB_PROPTXTSTATUS_SUPPR_SETSTATE(sc, 1);
			sc->first_sup_pkt = (WLPKTTAG(p)->wl_hdr_information & 0xff);

			SCB_PROPTXTSTATUS_SUPPR_SETGEN(sc, (SCB_PROPTXTSTATUS_SUPPR_GEN(sc) ^ 1));
		}
	}
	WLFC_PKTID_SETGEN(WLPKTTAG(p)->wl_hdr_information, SCB_PROPTXTSTATUS_SUPPR_GEN(sc));
	return rc;
}

void
wlc_process_wlhdr_txstatus(wlc_info_t *wlc, uint8 status_flag, void* p, bool hold)
{
	/* space for type(1), length(1) and value */
	uint8	results[1+1+WLFC_CTL_VALUE_LEN_TXSTATUS];
	uint32  statusdata = 0;
	uint32	wlhinfo;

	wlhinfo = WLPKTTAG(p)->wl_hdr_information;

	/* send txstatus only if this packet came from the host */
	if (WL_TXSTATUS_GET_FLAGS(wlhinfo) & WLFC_PKTFLAG_PKTFROMHOST) {
		if (!(WL_TXSTATUS_GET_FLAGS(wlhinfo) & WLFC_PKTFLAG_PKT_REQUESTED)) {
			/*
			if this packet was intended for AC FIFO and ac credit has not
			been sent back, push a credit here
			*/
			if (!(WLPKTTAG(p)->flags & WLF_CREDITED)) {
				wlfc_push_credit_data(wlc->wl, p);
			}
		}
		results[0] = WLFC_CTL_TYPE_TXSTATUS;
		results[1] = WLFC_CTL_VALUE_LEN_TXSTATUS;
		WL_TXSTATUS_SET_PKTID(statusdata, WL_TXSTATUS_GET_PKTID(wlhinfo));
		WL_TXSTATUS_SET_FLAGS(statusdata, status_flag);
		WLFC_PKTID_SETGEN(statusdata, WLFC_PKTID_GEN(wlhinfo));
		memcpy(&results[2], &statusdata, sizeof(uint32));
		wlfc_push_signal_data(wlc->wl, results, sizeof(results), hold);
		WLPKTTAG(p)->wl_hdr_information = 0;
		if (status_flag == WLFC_CTL_PKTFLAG_D11SUPPRESS) {
			wlfc_sendup_ctl_info_now(wlc->wl);
		}
		WLFC_COUNTER_TXSTATUS_COUNT(wlc);
	}
	else {
		WLFC_COUNTER_TXSTATUS_OTHER(wlc);
	}
	return;
}
#endif /* PROP_TXSTATUS */

void
wlc_recover_pkts(wlc_info_t *wlc, uint queue)
{
	void *p = NULL;
	volatile uint32 intstatus =
	        R_REG(wlc->osh, &wlc->regs->intctrlregs[queue].intstatus);

	if (intstatus) {
		if (!(intstatus & I_XI))
			WL_ERROR(("%s: failure 0x%x\t", __FUNCTION__, intstatus));

		while ((p = GETNEXTTXP(wlc, queue))) {
			wlc_txfifo_complete(wlc, queue, 1);
			PKTFREE(wlc->osh, p, TRUE);
		}
		W_REG(wlc->osh, &wlc->regs->intctrlregs[queue].intstatus, intstatus);
	}
	while ((p = dma_rx(WLC_HW_DI(wlc, RX_FIFO))))
		PKTFREE(wlc->osh, p, FALSE);
}

#ifdef WL_MULTIQUEUE
static struct scb*
wlc_recover_pkt_scb(wlc_info_t *wlc, void *pkt, wlc_txh_info_t *txh_info)
{
	struct scb *scb;
	wlc_bsscfg_t *bsscfg;

	scb = WLPKTTAG(pkt)->_scb;

	/* check to see if the SCB is just one of the permanent hwrs_scbs */
	if (scb == wlc->band->hwrs_scb ||
	    (NBANDS(wlc) > 1 && scb == wlc->bandstate[OTHERBANDUNIT(wlc)]->hwrs_scb)) {
		WL_MQ(("MQ: %s: recovering %p hwrs_scb\n",
		       __FUNCTION__, pkt));
		return scb;
	}

	/*  use the bsscfg index in the packet tag to find out which
	 *  bsscfg this packet belongs to
	 */
	bsscfg = wlc->bsscfg[WLPKTTAGBSSCFGGET(pkt)];
	/* unicast pkts have been seen with null bsscfg here */
	ASSERT(bsscfg || !ETHER_ISMULTI(txh_info->TxFrameRA));
	if (bsscfg == NULL) {
		WL_MQ(("MQ: %s: pkt cfg idx %d no longer exists\n",
			__FUNCTION__, WLPKTTAGBSSCFGGET(pkt)));
		scb = NULL;
	} else if (ETHER_ISMULTI(txh_info->TxFrameRA)) {
		scb = WLC_BCMCSCB_GET(wlc, bsscfg);
		ASSERT(scb != NULL);
		WL_MQ(("MQ: %s: recovering %p bcmc scb\n",
			__FUNCTION__, pkt));
	} else {
		uint16 mcl = ltoh16(txh_info->MacTxControlLow);

		scb = wlc_scbfindband(wlc, bsscfg, (struct ether_addr*)txh_info->TxFrameRA,
		                      ((mcl & TXC_FREQBAND_5G) == TXC_FREQBAND_5G) ?
		                      BAND_5G_INDEX : BAND_2G_INDEX);

#if defined(BCMDBG)
		if (scb != NULL) {
			char eabuf[ETHER_ADDR_STR_LEN];
			bcm_ether_ntoa(&scb->ea, eabuf);
			WL_MQ(("MQ: %s: recovering %p scb %p:%s\n", __FUNCTION__,
			       pkt, scb, eabuf));
		} else {
			WL_MQ(("MQ: %s: failed recovery scb for pkt %p\n", __FUNCTION__, pkt));
		}
#endif
	}

	WLPKTTAGSCBSET(pkt, scb);

	return scb;
}
#endif /* WL_MULTIQUEUE */

